import React from 'react';
import { tokens } from '../tokens';

// Professional Button component replacing scattered button styles
const Button = React.forwardRef(({
  children,
  variant = 'primary',
  size = 'md',
  disabled = false,
  loading = false,
  icon: Icon,
  iconPosition = 'left',
  className = '',
  ...props
}, ref) => {
  // Base styles using design tokens
  const baseStyles = {
    display: 'inline-flex',
    alignItems: 'center',
    justifyContent: 'center',
    fontFamily: tokens.typography.fontFamily.sans.join(', '),
    fontWeight: tokens.typography.fontWeight.medium,
    borderRadius: tokens.borderRadius.lg,
    transition: `all ${tokens.transitionDuration.fast} ${tokens.transitionTimingFunction.ease}`,
    cursor: disabled || loading ? 'not-allowed' : 'pointer',
    outline: 'none',
    border: '1px solid transparent',
  };

  // Size variants
  const sizes = {
    sm: {
      padding: `${tokens.spacing[2]} ${tokens.spacing[3]}`,
      fontSize: tokens.typography.fontSize.sm[0],
      lineHeight: tokens.typography.fontSize.sm[1].lineHeight,
    },
    md: {
      padding: `${tokens.spacing[3]} ${tokens.spacing[4]}`,
      fontSize: tokens.typography.fontSize.base[0],
      lineHeight: tokens.typography.fontSize.base[1].lineHeight,
    },
    lg: {
      padding: `${tokens.spacing[4]} ${tokens.spacing[6]}`,
      fontSize: tokens.typography.fontSize.lg[0],
      lineHeight: tokens.typography.fontSize.lg[1].lineHeight,
    },
  };

  // Variant styles
  const variants = {
    primary: {
      backgroundColor: tokens.colors.primary[500],
      color: tokens.colors.neutral[0],
      boxShadow: tokens.boxShadow.base,
      ...(disabled || loading ? {
        backgroundColor: tokens.colors.neutral[400],
        color: tokens.colors.neutral[600],
      } : {
        '&:hover': {
          backgroundColor: tokens.colors.primary[600],
          boxShadow: tokens.boxShadow.lg,
          transform: 'translateY(-1px)',
        },
        '&:active': {
          backgroundColor: tokens.colors.primary[700],
          transform: 'translateY(0)',
        },
        '&:focus': {
          boxShadow: `${tokens.boxShadow.base}, 0 0 0 3px rgba(255, 140, 26, 0.1)`,
        },
      }),
    },
    secondary: {
      backgroundColor: tokens.colors.dark.surface,
      color: tokens.colors.dark.text,
      borderColor: tokens.colors.dark.border,
      ...(disabled || loading ? {
        backgroundColor: tokens.colors.neutral[700],
        color: tokens.colors.neutral[500],
        borderColor: tokens.colors.neutral[600],
      } : {
        '&:hover': {
          backgroundColor: tokens.colors.neutral[700],
          borderColor: tokens.colors.primary[500],
        },
        '&:focus': {
          borderColor: tokens.colors.primary[500],
          boxShadow: `0 0 0 3px rgba(255, 140, 26, 0.1)`,
        },
      }),
    },
    success: {
      backgroundColor: tokens.colors.success[500],
      color: tokens.colors.neutral[0],
      ...(disabled || loading ? {
        backgroundColor: tokens.colors.neutral[400],
        color: tokens.colors.neutral[600],
      } : {
        '&:hover': {
          backgroundColor: tokens.colors.success[600],
        },
      }),
    },
    danger: {
      backgroundColor: tokens.colors.error[500],
      color: tokens.colors.neutral[0],
      ...(disabled || loading ? {
        backgroundColor: tokens.colors.neutral[400],
        color: tokens.colors.neutral[600],
      } : {
        '&:hover': {
          backgroundColor: tokens.colors.error[600],
        },
      }),
    },
    ghost: {
      backgroundColor: 'transparent',
      color: tokens.colors.dark.text,
      '&:hover': {
        backgroundColor: tokens.colors.dark.surface,
      },
    },
  };

  // Combine all styles
  const buttonStyles = {
    ...baseStyles,
    ...sizes[size],
    ...variants[variant],
  };

  // Loading spinner component
  const LoadingSpinner = () => (
    <svg
      className="animate-spin"
      width="16"
      height="16"
      viewBox="0 0 24 24"
      fill="none"
      xmlns="http://www.w3.org/2000/svg"
    >
      <circle
        cx="12"
        cy="12"
        r="10"
        stroke="currentColor"
        strokeWidth="4"
        strokeDasharray="32"
        strokeDashoffset="32"
        opacity="0.3"
      />
      <circle
        cx="12"
        cy="12"
        r="10"
        stroke="currentColor"
        strokeWidth="4"
        strokeDasharray="32"
        strokeDashoffset="24"
        strokeLinecap="round"
      />
    </svg>
  );

  return (
    <button
      ref={ref}
      style={buttonStyles}
      disabled={disabled || loading}
      className={className}
      {...props}
    >
      {loading && (
        <>
          <LoadingSpinner />
          <span style={{ marginLeft: tokens.spacing[2] }}>{children}</span>
        </>
      )}
      {!loading && Icon && iconPosition === 'left' && (
        <>
          <Icon size={size === 'sm' ? 16 : size === 'lg' ? 20 : 18} />
          <span style={{ marginLeft: tokens.spacing[2] }}>{children}</span>
        </>
      )}
      {!loading && !Icon && children}
      {!loading && Icon && iconPosition === 'right' && (
        <>
          {children}
          <span style={{ marginLeft: tokens.spacing[2] }}>
            <Icon size={size === 'sm' ? 16 : size === 'lg' ? 20 : 18} />
          </span>
        </>
      )}
    </button>
  );
});

Button.displayName = 'Button';

export default Button;
