// Redesigned CreateTask - Progressive disclosure wizard using design system
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import {
  Card,
  CardHeader,
  CardContent,
  Button,
  Badge,
  Heading1,
  Heading2,
  Text,
  TextSmall,
  GradientText,
  ArrowRightIcon,
  ArrowLeftIcon,
  CheckIcon,
  PlusIcon,
  CogIcon,
  tokens,
} from '../design-system';
import { useCreateTask } from '../hooks/api/useTasks';
import { useApp } from '../contexts/AppContext';
import { getApplicationTypes, getTechStacksForType, getTechStackById } from '../config/techStacks';
import RepositorySelector from '../components/RepositorySelector';

// Step components
const StepIndicator = ({ steps, currentStep }) => (
  <div style={{ 
    display: 'flex', 
    alignItems: 'center', 
    justifyContent: 'center',
    gap: tokens.spacing[2],
    marginBottom: tokens.spacing[6],
  }}>
    {steps.map((step, index) => {
      const isActive = index === currentStep;
      const isCompleted = index < currentStep;

      return (
        <div key={step.id} style={{ display: 'flex', alignItems: 'center' }}>
          <div style={{
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            width: '40px',
            height: '40px',
            borderRadius: tokens.borderRadius.full,
            backgroundColor: isCompleted 
              ? tokens.colors.success[500]
              : isActive 
                ? tokens.colors.primary[500]
                : tokens.colors.dark.border,
            color: (isActive || isCompleted) 
              ? tokens.colors.neutral[0] 
              : tokens.colors.dark.muted,
            fontWeight: tokens.typography.fontWeight.medium,
            fontSize: tokens.typography.fontSize.sm[0],
          }}>
            {isCompleted ? (
              <CheckIcon size={20} />
            ) : (
              index + 1
            )}
          </div>

          {index < steps.length - 1 && (
            <div style={{
              width: '40px',
              height: '2px',
              backgroundColor: index < currentStep 
                ? tokens.colors.success[500] 
                : tokens.colors.dark.border,
              margin: `0 ${tokens.spacing[2]}`,
            }} />
          )}
        </div>
      );
    })}
  </div>
);

// Step 1: Task Description
const DescriptionStep = ({ data, onUpdate, onNext }) => (
  <Card padding="lg">
    <CardHeader>
      <Heading2 style={{ margin: 0 }}>
        <span style={{ color: tokens.colors.primary[500], fontWeight: tokens.typography.fontWeight.semibold }}>What do you want to build?</span>
      </Heading2>
      <Text style={{ color: tokens.colors.dark.muted, margin: `${tokens.spacing[2]} 0 0 0` }}>
        Describe your project or task in detail. The more specific you are, the better our AI can help decompose it.
      </Text>
    </CardHeader>

    <CardContent>
      <div style={{ marginBottom: tokens.spacing[6] }}>
        <label style={{
          display: 'block',
          fontSize: tokens.typography.fontSize.sm[0],
          fontWeight: tokens.typography.fontWeight.medium,
          color: tokens.colors.dark.text,
          marginBottom: tokens.spacing[2],
        }}>
          Project Description *
        </label>
        <textarea
          value={data.description}
          onChange={(e) => onUpdate({ description: e.target.value })}
          placeholder="e.g., Build a task management app with real-time collaboration, user authentication, and drag-and-drop boards..."
          style={{
            width: '100%',
            minHeight: '120px',
            padding: tokens.spacing[3],
            borderRadius: tokens.borderRadius.md,
            border: `1px solid ${tokens.colors.dark.border}`,
            backgroundColor: tokens.colors.dark.surface,
            color: tokens.colors.dark.text,
            fontSize: tokens.typography.fontSize.sm[0],
            lineHeight: '1.5',
            resize: 'vertical',
          }}
          rows={5}
        />
      </div>

      <div style={{ 
        display: 'flex', 
        justifyContent: 'flex-end',
        gap: tokens.spacing[3],
      }}>
        <Button
          variant="primary"
          disabled={!data.description.trim()}
          onClick={onNext}
          icon={ArrowRightIcon}
        >
          Continue
        </Button>
      </div>
    </CardContent>
  </Card>
);

// Step 2: Tech Stack
const TechStackStep = ({ data, onUpdate, onNext, onPrev }) => {
  const applicationTypes = getApplicationTypes();
  const availableTechStacks = data.application_type ? getTechStacksForType(data.application_type) : [];
  const selectedTechStack = data.application_type && data.tech_stack_id 
    ? getTechStackById(data.application_type, data.tech_stack_id) 
    : null;

  const handleApplicationTypeChange = (e) => {
    const newType = e.target.value;
    onUpdate({
      application_type: newType,
      tech_stack_id: '', // Reset tech stack when application type changes
      tech_preferences: {
        ...data.tech_preferences,
        frontend_framework: '',
        backend_language: '',
        database: ''
      }
    });
  };

  const handleTechStackChange = (e) => {
    const stackId = e.target.value;
    const selectedStack = getTechStackById(data.application_type, stackId);

    onUpdate({
      tech_stack_id: stackId,
      tech_preferences: {
        ...data.tech_preferences,
        frontend_framework: selectedStack?.frontend_framework || '',
        backend_language: selectedStack?.backend_language || '',
        database: selectedStack?.database || ''
      }
    });
  };

  return (
    <Card padding="lg">
      <CardHeader>
        <Heading2 style={{ margin: 0 }}>
          <span style={{ color: tokens.colors.primary[500], fontWeight: tokens.typography.fontWeight.semibold }}>Choose Your Tech Stack</span>
        </Heading2>
        <Text style={{ color: tokens.colors.dark.muted, margin: `${tokens.spacing[2]} 0 0 0` }}>
          Choose from proven tech stack combinations, then customize individual components in "Additional Requirements" if needed. Our AI will adapt the recommendations to your specific needs.
        </Text>
      </CardHeader>

      <CardContent>
        <div style={{ 
          display: 'grid',
          gridTemplateColumns: '1fr',
          gap: tokens.spacing[4],
          marginBottom: tokens.spacing[6],
        }}>
          {/* Application Type */}
          <div>
            <label style={{
              display: 'block',
              fontSize: tokens.typography.fontSize.sm[0],
              fontWeight: tokens.typography.fontWeight.medium,
              color: tokens.colors.dark.text,
              marginBottom: tokens.spacing[2],
            }}>
              Type of Application *
            </label>
            <select
              value={data.application_type || ''}
              onChange={handleApplicationTypeChange}
              style={{
                width: '100%',
                padding: tokens.spacing[3],
                borderRadius: tokens.borderRadius.md,
                border: `1px solid ${tokens.colors.dark.border}`,
                backgroundColor: tokens.colors.dark.surface,
                color: tokens.colors.dark.text,
                fontSize: tokens.typography.fontSize.sm[0],
              }}
            >
              <option value="">Let AI choose application type</option>
              {applicationTypes.map(type => (
                <option key={type.id} value={type.id}>
                  {type.label} - {type.description}
                </option>
              ))}
            </select>
          </div>

          {/* Tech Stack - Only show if application type is selected */}
          {data.application_type && (
            <div>
              <label style={{
                display: 'block',
                fontSize: tokens.typography.fontSize.sm[0],
                fontWeight: tokens.typography.fontWeight.medium,
                color: tokens.colors.dark.text,
                marginBottom: tokens.spacing[2],
              }}>
                Tech Stack
              </label>
              <select
                value={data.tech_stack_id || ''}
                onChange={handleTechStackChange}
                style={{
                  width: '100%',
                  padding: tokens.spacing[3],
                  borderRadius: tokens.borderRadius.md,
                  border: `1px solid ${tokens.colors.dark.border}`,
                  backgroundColor: tokens.colors.dark.surface,
                  color: tokens.colors.dark.text,
                  fontSize: tokens.typography.fontSize.sm[0],
                }}
              >
                <option value="">Let AI choose tech stack</option>
                {availableTechStacks.map(stack => (
                  <option key={stack.id} value={stack.id}>
                    {stack.name} - {stack.description}
                  </option>
                ))}
              </select>
            </div>
          )}

          {/* Tech Stack Details - Show breakdown if tech stack is selected */}
          {selectedTechStack && (
            <div style={{
              padding: tokens.spacing[4],
              borderRadius: tokens.borderRadius.lg,
              backgroundColor: tokens.colors.dark.surface,
              border: `1px solid ${tokens.colors.dark.border}`,
            }}>
              <TextSmall style={{ 
                fontWeight: tokens.typography.fontWeight.medium,
                color: tokens.colors.dark.text,
                marginBottom: tokens.spacing[3],
              }}>
                Selected Tech Stack Details:
              </TextSmall>
              <div style={{ 
                display: 'grid',
                gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
                gap: tokens.spacing[3],
              }}>
                <div>
                  <TextSmall style={{ 
                    color: tokens.colors.dark.muted,
                    marginBottom: tokens.spacing[1],
                  }}>
                    Frontend
                  </TextSmall>
                  <Text style={{ color: tokens.colors.dark.text, margin: 0, fontSize: tokens.typography.fontSize.sm[0] }}>
                    {selectedTechStack.frontend_framework}
                  </Text>
                </div>
                <div>
                  <TextSmall style={{ 
                    color: tokens.colors.dark.muted,
                    marginBottom: tokens.spacing[1],
                  }}>
                    Backend
                  </TextSmall>
                  <Text style={{ color: tokens.colors.dark.text, margin: 0, fontSize: tokens.typography.fontSize.sm[0] }}>
                    {selectedTechStack.backend_language}
                  </Text>
                </div>
                <div>
                  <TextSmall style={{ 
                    color: tokens.colors.dark.muted,
                    marginBottom: tokens.spacing[1],
                  }}>
                    Database
                  </TextSmall>
                  <Text style={{ color: tokens.colors.dark.text, margin: 0, fontSize: tokens.typography.fontSize.sm[0] }}>
                    {selectedTechStack.database}
                  </Text>
                </div>
              </div>
            </div>
          )}
        </div>

        {/* Additional Requirements */}
        <div style={{ marginBottom: tokens.spacing[6] }}>
          <label style={{
            display: 'block',
            fontSize: tokens.typography.fontSize.sm[0],
            fontWeight: tokens.typography.fontWeight.medium,
            color: tokens.colors.dark.text,
            marginBottom: tokens.spacing[2],
          }}>
            Additional Requirements & Overrides
          </label>
          <Text style={{
            color: tokens.colors.dark.muted,
            fontSize: tokens.typography.fontSize.xs[0],
            marginBottom: tokens.spacing[2],
            margin: `0 0 ${tokens.spacing[2]} 0`,
          }}>
            Specify additional requirements or override specific tech stack components (e.g., "Use Kafka instead of Redis for messaging", "No database needed", "Use TypeScript", "Add real-time features")
          </Text>
          <textarea
            value={data.tech_preferences.additional_requirements}
            onChange={(e) => onUpdate({
              tech_preferences: {
                ...data.tech_preferences,
                additional_requirements: e.target.value
              }
            })}
            placeholder="e.g., Use Kafka for messaging instead of Redis, implement real-time WebSocket features, must be mobile-responsive, use TypeScript throughout..."
            style={{
              width: '100%',
              minHeight: '100px',
              padding: tokens.spacing[3],
              borderRadius: tokens.borderRadius.md,
              border: `1px solid ${tokens.colors.dark.border}`,
              backgroundColor: tokens.colors.dark.surface,
              color: tokens.colors.dark.text,
              fontSize: tokens.typography.fontSize.sm[0],
              lineHeight: '1.5',
              resize: 'vertical',
            }}
            rows={4}
          />
        </div>

        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between',
          gap: tokens.spacing[3],
        }}>
          <Button
            variant="secondary"
            onClick={onPrev}
            icon={ArrowLeftIcon}
          >
            Previous
          </Button>
          <Button
            variant="primary"
            onClick={onNext}
            icon={ArrowRightIcon}
          >
            Continue
          </Button>
        </div>
      </CardContent>
    </Card>
  );
};

// Step 3: Advanced Configuration
const ConfigurationStep = ({ data, onUpdate, onNext, onPrev }) => (
  <Card padding="lg">
    <CardHeader>
      <Heading2 style={{ margin: 0 }}>
        <span style={{ color: tokens.colors.primary[500], fontWeight: tokens.typography.fontWeight.semibold }}>Fine-tune AI Parameters</span>
      </Heading2>
      <Text style={{ color: tokens.colors.dark.muted, margin: `${tokens.spacing[2]} 0 0 0` }}>
        Configure how our AI will decompose your task. These settings control the depth and complexity of the breakdown.
      </Text>
    </CardHeader>

    <CardContent>
      <div style={{ 
        display: 'grid',
        gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))',
        gap: tokens.spacing[6],
        marginBottom: tokens.spacing[6],
      }}>
        {/* Max Depth */}
        <div>
          <label style={{
            display: 'block',
            fontSize: tokens.typography.fontSize.sm[0],
            fontWeight: tokens.typography.fontWeight.medium,
            color: tokens.colors.dark.text,
            marginBottom: tokens.spacing[2],
          }}>
            Maximum Depth
          </label>
          <select
            value={data.max_depth}
            onChange={(e) => onUpdate({ max_depth: parseInt(e.target.value) })}
            style={{
              width: '100%',
              padding: tokens.spacing[3],
              borderRadius: tokens.borderRadius.md,
              border: `1px solid ${tokens.colors.dark.border}`,
              backgroundColor: tokens.colors.dark.surface,
              color: tokens.colors.dark.text,
              fontSize: tokens.typography.fontSize.sm[0],
            }}
          >
            <option value={2}>2 levels - Quick breakdown</option>
            <option value={3}>3 levels - Balanced detail</option>
            <option value={4}>4 levels - Detailed analysis</option>
            <option value={5}>5 levels - Maximum depth</option>
          </select>
        </div>

        {/* Max Subtasks */}
        <div>
          <label style={{
            display: 'block',
            fontSize: tokens.typography.fontSize.sm[0],
            fontWeight: tokens.typography.fontWeight.medium,
            color: tokens.colors.dark.text,
            marginBottom: tokens.spacing[2],
          }}>
            Max Subtasks per Level
          </label>
          <select
            value={data.max_subtasks}
            onChange={(e) => onUpdate({ max_subtasks: parseInt(e.target.value) })}
            style={{
              width: '100%',
              padding: tokens.spacing[3],
              borderRadius: tokens.borderRadius.md,
              border: `1px solid ${tokens.colors.dark.border}`,
              backgroundColor: tokens.colors.dark.surface,
              color: tokens.colors.dark.text,
              fontSize: tokens.typography.fontSize.sm[0],
            }}
          >
            <option value={5}>5 subtasks - Concise</option>
            <option value={10}>10 subtasks - Standard</option>
            <option value={15}>15 subtasks - Comprehensive</option>
            <option value={20}>20 subtasks - Exhaustive</option>
          </select>
        </div>
      </div>

      {/* Complexity Threshold */}
      <div style={{ marginBottom: tokens.spacing[6] }}>
        <label style={{
          display: 'block',
          fontSize: tokens.typography.fontSize.sm[0],
          fontWeight: tokens.typography.fontWeight.medium,
          color: tokens.colors.dark.text,
          marginBottom: tokens.spacing[2],
        }}>
          Complexity Threshold: {data.complexity_threshold}
        </label>
        <input
          type="range"
          min="0.1"
          max="1.0"
          step="0.1"
          value={data.complexity_threshold}
          onChange={(e) => onUpdate({ complexity_threshold: parseFloat(e.target.value) })}
          style={{
            width: '100%',
            height: '4px',
            borderRadius: tokens.borderRadius.full,
            backgroundColor: tokens.colors.dark.border,
            outline: 'none',
            appearance: 'none',
            cursor: 'pointer',
          }}
        />
        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between', 
          fontSize: tokens.typography.fontSize.xs[0],
          color: tokens.colors.dark.muted,
          marginTop: tokens.spacing[1],
        }}>
          <span>Simple tasks</span>
          <span>Complex tasks</span>
        </div>
        <TextSmall style={{ 
          color: tokens.colors.dark.muted,
          marginTop: tokens.spacing[2],
          margin: `${tokens.spacing[2]} 0 0 0`,
        }}>
          Lower values create more detailed subtasks, higher values keep tasks more general.
        </TextSmall>
      </div>

      <div style={{ 
        display: 'flex', 
        justifyContent: 'space-between',
        gap: tokens.spacing[3],
      }}>
        <Button
          variant="secondary"
          onClick={onPrev}
          icon={ArrowLeftIcon}
        >
          Previous
        </Button>
        <Button
          variant="primary"
          onClick={onNext}
          icon={ArrowRightIcon}
        >
          Continue
        </Button>
      </div>
    </CardContent>
  </Card>
);

// Step 4: Review and Create
const ReviewStep = ({ data, onUpdate, onPrev, onSubmit, isLoading }) => {
  const applicationTypeLabel = data.application_type 
    ? getApplicationTypes().find(type => type.id === data.application_type)?.label
    : null;
  const selectedTechStack = data.application_type && data.tech_stack_id 
    ? getTechStackById(data.application_type, data.tech_stack_id) 
    : null;

  return (
    <Card padding="lg">
      <CardHeader>
        <Heading2 style={{ margin: 0 }}>
          <span style={{ color: tokens.colors.primary[500], fontWeight: tokens.typography.fontWeight.semibold }}>Review & Create</span>
        </Heading2>
        <Text style={{ color: tokens.colors.dark.muted, margin: `${tokens.spacing[2]} 0 0 0` }}>
          Review your task configuration and create your AI-powered project breakdown.
        </Text>
      </CardHeader>

      <CardContent>
        {/* Task Description */}
        <div style={{ 
          padding: tokens.spacing[4],
          borderRadius: tokens.borderRadius.lg,
          backgroundColor: tokens.colors.dark.surface,
          border: `1px solid ${tokens.colors.dark.border}`,
          marginBottom: tokens.spacing[4],
        }}>
          <Text style={{
            fontSize: tokens.typography.fontSize.sm[0],
            fontWeight: tokens.typography.fontWeight.medium,
            color: tokens.colors.dark.text,
            marginBottom: tokens.spacing[2],
          }}>
            Task Description
          </Text>
          <Text style={{
            color: tokens.colors.dark.muted,
            lineHeight: '1.5',
            margin: 0,
          }}>
            {data.description}
          </Text>
        </div>

        {/* GitHub Repository */}
        <div style={{ marginBottom: tokens.spacing[6] }}>
          <Text style={{
            fontSize: tokens.typography.fontSize.sm[0],
            fontWeight: tokens.typography.fontWeight.medium,
            color: tokens.colors.dark.text,
            marginBottom: tokens.spacing[2],
          }}>
            Repository Context (Optional)
          </Text>
          <Text size="sm" style={{ 
            color: tokens.colors.dark.muted,
            marginBottom: tokens.spacing[3]
          }}>
            Select a repository to provide codebase context for better task decomposition
          </Text>
          <RepositorySelector
            value={data.repository}
            onChange={(repo) => onUpdate({ repository: repo })}
          />
        </div>

        {/* Application Type and Tech Stack */}
        <div style={{ 
          padding: tokens.spacing[4],
          borderRadius: tokens.borderRadius.lg,
          backgroundColor: tokens.colors.dark.surface,
          border: `1px solid ${tokens.colors.dark.border}`,
          marginBottom: tokens.spacing[4],
        }}>
          <Text style={{
            fontSize: tokens.typography.fontSize.sm[0],
            fontWeight: tokens.typography.fontWeight.medium,
            color: tokens.colors.dark.text,
            marginBottom: tokens.spacing[3],
          }}>
            Application & Tech Stack
          </Text>

          <div style={{ 
            display: 'grid',
            gridTemplateColumns: '1fr 1fr',
            gap: tokens.spacing[4],
            marginBottom: selectedTechStack ? tokens.spacing[3] : 0,
          }}>
            <div>
              <TextSmall style={{ 
                color: tokens.colors.dark.muted,
                marginBottom: tokens.spacing[1],
              }}>
                Application Type
              </TextSmall>
              <Text style={{ color: tokens.colors.dark.text, margin: 0 }}>
                {applicationTypeLabel || 'AI will choose'}
              </Text>
            </div>

            <div>
              <TextSmall style={{ 
                color: tokens.colors.dark.muted,
                marginBottom: tokens.spacing[1],
              }}>
                Tech Stack
              </TextSmall>
              <Text style={{ color: tokens.colors.dark.text, margin: 0 }}>
                {selectedTechStack ? selectedTechStack.name : 'AI will choose'}
              </Text>
            </div>
          </div>

          {/* Tech Stack Details */}
          {selectedTechStack && (
            <div style={{ 
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(150px, 1fr))',
              gap: tokens.spacing[3],
              paddingTop: tokens.spacing[3],
              borderTop: `1px solid ${tokens.colors.dark.border}`,
            }}>
              <div>
                <TextSmall style={{ 
                  color: tokens.colors.dark.muted,
                  marginBottom: tokens.spacing[1],
                }}>
                  Frontend
                </TextSmall>
                <Text style={{ color: tokens.colors.dark.text, margin: 0, fontSize: tokens.typography.fontSize.sm[0] }}>
                  {selectedTechStack.frontend_framework}
                </Text>
              </div>
              <div>
                <TextSmall style={{ 
                  color: tokens.colors.dark.muted,
                  marginBottom: tokens.spacing[1],
                }}>
                  Backend
                </TextSmall>
                <Text style={{ color: tokens.colors.dark.text, margin: 0, fontSize: tokens.typography.fontSize.sm[0] }}>
                  {selectedTechStack.backend_language}
                </Text>
              </div>
              <div>
                <TextSmall style={{ 
                  color: tokens.colors.dark.muted,
                  marginBottom: tokens.spacing[1],
                }}>
                  Database
                </TextSmall>
                <Text style={{ color: tokens.colors.dark.text, margin: 0, fontSize: tokens.typography.fontSize.sm[0] }}>
                  {selectedTechStack.database}
                </Text>
              </div>
            </div>
          )}
        </div>

        {/* Additional Requirements */}
        {data.tech_preferences.additional_requirements && (
          <div style={{ 
            padding: tokens.spacing[4],
            borderRadius: tokens.borderRadius.lg,
            backgroundColor: tokens.colors.dark.surface,
            border: `1px solid ${tokens.colors.dark.border}`,
            marginBottom: tokens.spacing[4],
          }}>
            <Text style={{
              fontSize: tokens.typography.fontSize.sm[0],
              fontWeight: tokens.typography.fontWeight.medium,
              color: tokens.colors.dark.text,
              marginBottom: tokens.spacing[2],
            }}>
              Additional Requirements
            </Text>
            <Text style={{
              color: tokens.colors.dark.muted,
              lineHeight: '1.5',
              margin: 0,
            }}>
              {data.tech_preferences.additional_requirements}
            </Text>
          </div>
        )}

        {/* Configuration */}
        <div style={{ 
          display: 'flex',
          gap: tokens.spacing[4],
          marginBottom: tokens.spacing[6],
          flexWrap: 'wrap',
        }}>
          <Badge variant="info" size="lg">
            Max Depth: {data.max_depth}
          </Badge>
          <Badge variant="info" size="lg">
            Max Subtasks: {data.max_subtasks}
          </Badge>
          <Badge variant="info" size="lg">
            Complexity: {data.complexity_threshold}
          </Badge>
        </div>

        {/* Approval Checkbox */}
        <div style={{ 
          padding: tokens.spacing[4],
          borderRadius: tokens.borderRadius.lg,
          backgroundColor: tokens.colors.dark.surface,
          border: `1px solid ${tokens.colors.dark.border}`,
          marginBottom: tokens.spacing[4],
        }}>
          <label style={{
            display: 'flex',
            alignItems: 'center',
            gap: tokens.spacing[3],
            cursor: 'pointer',
          }}>
            <input
              type="checkbox"
              checked={data.requires_approval}
              onChange={(e) => onUpdate({ requires_approval: e.target.checked })}
              style={{
                width: '18px',
                height: '18px',
                accentColor: tokens.colors.primary[500],
              }}
            />
            <div>
              <Text style={{
                fontSize: tokens.typography.fontSize.sm[0],
                fontWeight: tokens.typography.fontWeight.medium,
                color: tokens.colors.dark.text,
                margin: 0,
              }}>
                Review decomposition before processing
              </Text>
              <TextSmall style={{
                color: tokens.colors.dark.muted,
                margin: `${tokens.spacing[1]} 0 0 0`,
              }}>
                Pause after task decomposition to review and approve suggested subtasks
              </TextSmall>
            </div>
          </label>
        </div>

        <div style={{ 
          display: 'flex', 
          justifyContent: 'space-between',
          gap: tokens.spacing[3],
        }}>
          <Button
            variant="secondary"
            onClick={onPrev}
            icon={ArrowLeftIcon}
            disabled={isLoading}
          >
            Previous
          </Button>
          <Button
            variant="primary"
            onClick={onSubmit}
            icon={PlusIcon}
            loading={isLoading}
          >
            Create & Decompose
          </Button>
        </div>
      </CardContent>
    </Card>
  );
};

// Main CreateTask component
const CreateTask = () => {
  const navigate = useNavigate();
  const { showSuccess, showError } = useApp();
  const { mutate: createTask, isPending: isCreating } = useCreateTask();

  const [currentStep, setCurrentStep] = useState(0);
  const [formData, setFormData] = useState({
    description: '',
    application_type: '',
    tech_stack_id: '',
    max_depth: 3,
    max_subtasks: 10,
    complexity_threshold: 0.7,
    use_context: true,
    requires_approval: false,
    tech_preferences: {
      frontend_framework: '',
      backend_language: '',
      database: '',
      additional_requirements: ''
    }
  });

  const steps = [
    { id: 'description', title: 'Description' },
    { id: 'tech-stack', title: 'Tech Stack' },
    { id: 'configuration', title: 'Configuration' },
    { id: 'review', title: 'Review' },
  ];

  const handleUpdate = (updates) => {
    setFormData(prev => ({
      ...prev,
      ...updates
    }));
  };

  const handleNext = () => {
    if (currentStep < steps.length - 1) {
      setCurrentStep(currentStep + 1);
    }
  };

  const handlePrev = () => {
    if (currentStep > 0) {
      setCurrentStep(currentStep - 1);
    }
  };

  const handleSubmit = () => {
    // Restructure data to match API expectations - move application_type and tech_stack_id into tech_preferences
    const submissionData = {
      ...formData,
      tech_preferences: {
        ...formData.tech_preferences,
        application_type: formData.application_type,
        tech_stack_id: formData.tech_stack_id,
      },
      // Remove from top level since they should be inside tech_preferences
      application_type: undefined,
      tech_stack_id: undefined,
      // Add repository information if selected
      repository: formData.repository || null,
    };

    createTask(submissionData, {
      onSuccess: (response) => {
        // Navigate immediately to show the task with decomposition feedback
        navigate(`/tasks/${response.task_id}`, { 
          state: { 
            justCreated: true, 
            showDecompositionFeedback: true 
          } 
        });
      },
      onError: (error) => {
        showError(`Failed to create task: ${error.message}`);
      },
    });
  };

  const renderStep = () => {
    switch (currentStep) {
      case 0:
        return (
          <DescriptionStep
            data={formData}
            onUpdate={handleUpdate}
            onNext={handleNext}
          />
        );
      case 1:
        return (
          <TechStackStep
            data={formData}
            onUpdate={handleUpdate}
            onNext={handleNext}
            onPrev={handlePrev}
          />
        );
      case 2:
        return (
          <ConfigurationStep
            data={formData}
            onUpdate={handleUpdate}
            onNext={handleNext}
            onPrev={handlePrev}
          />
        );
      case 3:
        return (
          <ReviewStep
            data={formData}
            onUpdate={handleUpdate}
            onPrev={handlePrev}
            onSubmit={handleSubmit}
            isLoading={isCreating}
          />
        );
      default:
        return null;
    }
  };

  return (
    <div className="container" style={{ paddingTop: tokens.spacing[6] }}>
      {/* Header */}
      <header style={{ marginBottom: tokens.spacing[8] }}>
        <div style={{ textAlign: 'center' }}>
          <Heading1 style={{ margin: 0, color: tokens.colors.primary[500] }}>
            Create New Task
          </Heading1>
          <Text style={{ 
            color: tokens.colors.dark.muted,
            marginTop: tokens.spacing[2],
            margin: `${tokens.spacing[2]} 0 0 0`,
            maxWidth: '600px',
            marginLeft: 'auto',
            marginRight: 'auto',
          }}>
            Leverage AI to decompose complex projects into manageable subtasks with our guided wizard
          </Text>
        </div>
      </header>

      {/* Step Indicator */}
      <StepIndicator steps={steps} currentStep={currentStep} />

      {/* Step Content */}
      <div style={{ maxWidth: '800px', margin: '0 auto' }}>
        {renderStep()}
      </div>
    </div>
  );
};

export default CreateTask;
